func TestTwo(t *testing.T) {
	fallback := `[{"anonymous":false,"inputs":[{"indexed":false,"internalType":"string","name":"message","type":"string"},{"indexed":true,"internalType":"string","name":"extradata","type":"string"},{"indexed":false,"internalType":"uint256","name":"val","type":"uint256"}],"name":"UpdatedValue","type":"event"},{"inputs":[],"name":"get","outputs":[{"internalType":"uint256","name":"retVal","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"uint256","name":"x","type":"uint256"}],"name":"set","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[],"name":"storedData","outputs":[{"internalType":"uint256","name":"","type":"uint256"}],"stateMutability":"view","type":"function"}]`

	abiq, err := abi.JSON(strings.NewReader(fallback))
	if !assert.Nil(t, err) {
		return
	}

	for _, meth := range abiq.Events {
		//fmt.Println(meth.String())
		//fmt.Println(meth.Sig())
		//fmt.Println(common.Bytes2Hex(meth.ID()))
		fmt.Println(meth.ID().String())
		fmt.Println()
	}

	fmt.Println(abiq)
}
